            <h3>Majorizer</h3>
            <nav ref={navRef}>
                <a href="/#">Home</a>
                <a href="/#">Login</a>
                <button className="nav-btn nav-close-btn" onClick={showNavBar}>
                    <FaTimes>
                    </FaTimes>
                </button>
            </nav>
            <button className="nav-btn" onClick={showNavBar}>
                <FaBars>
                </FaBars>
            </button>
        </header>
