//This is the code for our home page
import React, {useContext} from 'react'
import {useNavigate} from 'react-router-dom'
import AuthContext from '../context/AuthContext.js'
import './styles/HomePage.css'
window.$mwf1='CS141';
window.$mwf2='MA131';
window.$mwf3='';
window.$tt1= 'PY151';
window.$tt2= '';
window.$tt3 = "PY153";

class ScheduleForm extends React.Component
{
constructor(props){
    super(props);
    this.state = {value: 'Schedule 1'}
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(event){
        this.setState({value: event.target.value});
    }
    handleSubmit(event){
        if (this.state.value === 'S1') {
            window.$mwf1='CS141';
            window.$mwf2='MA131';
            window.$mwf3='';
            window.$tt1= 'PY151';
            window.$tt2= '';
            window.$tt3 = "PY153";
        }
        if (this.state.value === 'S2') {
            window.$mwf1 = 'CS241';
            window.$mwf2 = '';
            window.$mwf3='MA132';
            window.$tt1 = 'PY153';
            window.$tt2 = 'PY155';
            window.$tt3 = "CS242";
        }
        this.setState({value: event.target.value});

        event.preventDefault();
    }
    render(){return(
            <div>
            <form onSubmit={this.handleSubmit}>
            <label>
              <h2>Choose a schedule:</h2>
              <select id = 'selection' value={this.state.value} onChange={this.handleChange}>
                <option value="S1">Schedule 1</option>
                <option value="S2">Schedule 2</option>
                <option value="S3">Schedule 3</option>
                <option value="S4">Schedule 4</option>
                <option value="S5">Schedule 5</option>
                <option value="S6">Schedule 6</option>
                <option value="S7">Schedule 7</option>
                <option value="S8">Schedule 8</option>
              </select>
            </label>
            <input id='selectbutton' type="submit" value="Select" />
          </form>
          <table className='schedule'>
                <tbody>
                <tr>
                    <th>Mon</th>
                    <th>Tues</th>
                    <th>Wed</th>
                    <th>Thurs</th>
                    <th>Fri</th>
                </tr>
                <tr>
                    <td>{window.$mwf1}</td>
                    <td>{window.$tt1}</td>
                    <td>{window.$mwf1}</td>
                    <td>{window.$tt1}</td>
                    <td>{window.$mwf1}</td>
                </tr>
                <tr>
                    <td>{window.$mwf2}</td>
                    <td>{window.$tt2}</td>
                    <td>{window.$mwf2}</td>
                    <td>{window.$tt2}</td>
                    <td>{window.$mwf2}</td>
                </tr>
                <tr>
                    <td>{window.$mwf3}</td>
                    <td>{window.$tt3}</td>
                    <td>{window.$mwf3}</td>
                    <td>{window.$tt3}</td>
                    <td>{window.$mwf3}</td>
                </tr>
                </tbody>
            </table>

          </div>
        );
    }
}

const Adv1Display = () =>{
    return (
        <div className='studentList'>
            <p className='studentListHeader'>Students you are Advising:</p>
        <p className='studentName'>John Doe</p>
            <p className='studMajorAndYear'>Computer Science Major, 2023</p>
        <p className='studentName'>Jane Doe</p>
            <p className='studMajorAndYear'>Computer Science Major, Math Minor, 2024</p>
        </div>
    )
}

const Adv2Display = () =>{
    return (
        <div className='studentList'>
            <p className='studentListHeader'>Students you are Advising:</p>
        <p className='studentName'>Josh Doe</p>
            <p className='studMajorAndYear'>Psychology Major, 2024</p>
        <p className='studentName'>James Doe</p>
            <p className='studMajorAndYear'>Psychology Major, Math Minor, 2025</p>
        </div>
    )
}


const StudHome = () =>{
    return(
    <div>
        <p>Your Most Recent Schedule: </p>
        <ScheduleForm id = 'stuschedule'></ScheduleForm>
    </div>)
}

const AdvHome = () =>{
    let {user} = useContext(AuthContext)

    return(
    <div>
       {/*The below block checks for a user and renders the correct nave bar based on who is logged in*/
            user === null ? (<h3>You are not an advisor and something has gone wrong</h3>) :
            user.username === 'advisor1' ? (<Adv1Display />) :
            user.username === 'advisor2' ? (<Adv2Display />) :
            null}
    </div>
    )
}

const AdmHome = () =>{
    return(
    <div>
        <p className='activeRequestField'>You have 2 active requests!</p>
    </div>)
}
//this will be edited to link with backend stuff later on, hardcoding in 2 for rn


const HomePage = () => {
    let {user} = useContext(AuthContext)

    return (
        <div>
            <p className='welcomeField'>Hello {user.username}: {user.group} account</p>
            {/*The below block checks for a user and renders the correct nave bar based on who is logged in*/
            user === null ? (<h3>WELCOME!</h3>) :
            user.group === 'student' ? (<StudHome />) :
            user.group === 'advisor' ? (<AdvHome />) :
            user.group === 'admin' ? (<AdmHome />) : 
            null}

        </div>
    )
}

export default HomePage;
