//This is the code for our home page
import React, {useContext} from 'react'
import { useResolvedPath } from 'react-router-dom'
import AuthContext from '../context/AuthContext.js'
import './styles/HomePage.css'


const StudHome = () =>{
    return(
    <div>
        let {userS} = useContext(AuthContext)
        <p className='welcomeField'>Hello {userS.username} you are a {userS.group}</p>
    </div>)
}

const AdvHome = () =>{
    return(
    <div>
        let {userAv} = useContext(AuthContext)
        <p className='welcomeField'>Hello {userAv.username} you are a {userAv.group}</p>
    </div>)
}

const AdmHome = () =>{
    return(
    <div>
        let {userAm} = useContext(AuthContext)
        <p className='welcomeField'>Hello {userAm.username} you are a {userAm.group}</p>
    </div>)
}

const HomePage = () => {
    let {user} = useContext(AuthContext)

    return (
        <div>
            {/*The below block checks for a user and renders the correct nave bar based on who is logged in*/
            user === null ? (<h3>WELCOME!</h3>) :
            user.group === 'student' ? (<StudHome />) :
            user.group === 'advisor' ? (<AdvHome />) :
            user.group === 'admin' ? (<AdmHome />) : 
            null}

        </div>
    )
}

export default HomePage;
